import React from 'react';
import styled from 'styled-components';
import {Link} from "react-scroll";

const Ul = styled.ul`
  z-index: 500;
  list-style: none;
  display: flex;
  flex-flow: row nowrap;
  li {
    padding: 18px 10px;
  }
  @media (max-width: 768px) {
    flex-flow: column nowrap;
    background-color: #997d4f;
    position: fixed;
    transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(100%)'};
    top: 0;
    right: 0;
    height: 100vh;
    width: 300px;
    padding-top: 3.5rem;
    transition: transform 0.3s ease-in-out;
    li {
      color: #fff;

  }
`
const ListItem = styled.li`
list-style:none;
color:white;
margin:0px 0px
transition: ease-in 200ms;
height:auto;
text-transform:uppercase;
font-size:12px;


`

const ListItemline = styled.li`
list-style:none;
color:white;
margin:0px 0px
height:auto;
@media (max-width: 768px) {
 display:none;}
`
;

const RightNav = ({ open }) => {
  return (
    <Ul open={open}>


      <Link 
        activeClass="active"
        to="Home"
        spy={true}
        smooth={true}
        offset={0}
        duration= {250}
        ><ListItem >Home</ListItem>

      </Link>

      &nbsp;
        <ListItemline >|</ListItemline>
        &nbsp;

      <Link
        activeClass="active"
        to="AboutUs"
        spy={true}
        smooth={true}
        offset={-40}
        duration= {250}
        ><ListItem >ABOUT</ListItem>
      </Link>  


        &nbsp;
        <ListItemline >|</ListItemline>
        &nbsp;

      <Link
        activeClass="active"
        to="Weddings"
        spy={true}
        smooth={true}
        offset={-40}
        duration= {250}
        ><ListItem >Packages</ListItem>
      </Link>

      &nbsp;
        <ListItemline >|</ListItemline>
        &nbsp;

      <Link 
        activeClass="active"
        to="ContactMe"
        spy={true}
        smooth={true}
        offset={-40}
        duration= {250}
        ><ListItem >Contact</ListItem>
      </Link>



    </Ul>
  )
}

export default RightNav
