    render(){ 
        return ( 
            <Container id="AboutUs">
                <Holder className="headings">
          <i class="fa fa-bell-o"></i><Heading className="headings">{this.props.heading}</Heading>
          <i class="fa fa-bell-o"></i>
           </Holder>
                <Sub>{this.props.sub}</Sub>
                <br/>
