import React, { Component } from 'react';
import styled from 'styled-components';
import AOS from 'aos';
import 'aos/dist/aos.css';
import Top from '../images/over.png'
const Container = styled.div`
background: var(--Section-color);
text-align: center;
z-index:1;
position:relative;
`

const Heading = styled.h2`
color: var(--secondary-color);
margin: 0px;
text-align: center;
padding: 5px 5px;
`
const Sub = styled.p`
color: white;
margin: 0px;
text-align: center;
`

const Para = styled.p`
max-width:90%
color:white
text-align:center;
display:block
margin:0 auto;
padding-bottom:20px;
@media(max-width:768px){

}
`

