class TextBanner extends Component {
    render(){ 
        return ( 
            <Container id="AboutUs">
                <Holder>
           <Anchor href="" class="icon1"><i class="fa fa-bell-o"></i></Anchor><Heading className="headings">{this.props.heading}</Heading>
           <Anchor href="" class="icon1"><i class="fa fa-bell-o"></i></Anchor>
           </Holder>
                <Sub>{this.props.sub}</Sub>
                <br/>
                <Para>{this.props.paragraph1}</Para>

                <Para>{this.props.paragraph2}</Para>

                <Para>{this.props.paragraph3}</Para>
                <br/>
                <br/>
            </Container>
        );
    }
}

export default TextBanner;
