import React, { Component } from 'react';
import styled from 'styled-components';

const Container =styled.div`
background-color:var(--transparent);
border:none;
border-radius:10px;
color:white;
display: flex;
justify-content: center;
@media(max-width:678px){
    margin: 0 auto;
    width: 100%;
    display: flex;
    justify-content: center;
}
`

const Form =styled.div`
display:flex;
flex-direction:column;
width: 100vw;
justify-content:space-around;
@media(max-width:425px){

}
`

const Input =styled.input`
background-color:transparent;
border:none;
border-bottom:2px solid var(--Section-color);
min-height:40px;
margin-bottom:5px;
color:aliceblue;
`

const Label =styled.label`
display:flex;
flex-direction:column;
color:aliceblue;
`

const Textarea =styled.textarea`
background-color:aliceblue;
border:none;
margin-bottom:20px;
color:black;
margin-top:10px;
`

const Field =styled.fieldset`

`

const Button =styled.button`
background-color:var(--primary-color);
border:1px white solid;
color:var(--secondary-color);;
text-transform:uppercase;
padding:10px 100px;
width:fit-content;
margin:0 auto;
margin-top:10px
font-weight:700;
@media(max-width:425px){
    width: 100%;
}
&:hover{
    background:var(--Section-color)

  }
`

class Forms extends Component {

    render() { 
        return (
           <Container>
                <form method="post" action="contact.php" name="contactform" id="contactform" class="form c-form">
					<fieldset>
						<input className="InputText" name="name" type="text" id="name" placeholder="Your Name" />
                        <br/>
						<input name="email" type="text" id="email" placeholder="Your E-mail" />
                        <br/>
						<textarea name="comments" id="comments" placeholder="Message"></textarea>
                        <br/>
						<input name="verify" type="text" id="verify" placeholder="How much is 3 + 1 =" />
                        <br/>
						<input type="submit" class="submit btn outline" id="submit" value="Send message" />
					</fieldset>
				</form>
            </Container>
         );
    }
}

export default Forms;
